package com.ochibooh.demo.rmd.data.http.request.user;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.ToString;import java.io.Serializable;@ToString@Getter@NoArgsConstructor@AllArgsConstructorpublic class UserCreateRequest implements Serializable {    @JsonProperty(value = "email")    private String email;    @JsonProperty(value = "first_name")    private String firstName;    @JsonProperty(value = "last_name")    private String lastName;    @JsonProperty(value = "role")    private Long role;    @JsonProperty(value = "password")    private String password;}