package com.ochibooh.demo.rmd.handler;import lombok.extern.java.Log;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;import reactor.core.publisher.Mono;import java.util.logging.Level;@Log@RestController@RequestMapping(        path = {"/cb"})public class CallbackHandler {    @RequestMapping(            path = "/test",            method = RequestMethod.POST,            consumes = MediaType.APPLICATION_JSON_VALUE,            produces = MediaType.APPLICATION_JSON_VALUE    )    public Mono<ResponseEntity<String>> test(            @RequestBody Object body    ) {        log.log(Level.INFO, String.format("Test Cb body [ %s ]", body));        return Mono.just(ResponseEntity.ok().body("Success"));    }}