package com.ochibooh.demo.rmd.model;import com.fasterxml.jackson.annotation.JsonFormat;import com.ochibooh.demo.rmd.convereter.AccessRightConverter;import com.ochibooh.demo.rmd.integ.RoleService;import lombok.Data;import lombok.EqualsAndHashCode;import lombok.ToString;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.DynamicUpdate;import org.hibernate.annotations.UpdateTimestamp;import javax.persistence.*;import java.io.Serializable;import java.sql.Timestamp;import java.util.List;@ToString@Data@EqualsAndHashCode(of = {"id"})@DynamicUpdate@Entity@Table(name = "CONFIG_ROLE", uniqueConstraints = {        @UniqueConstraint(name = "CONFIG_ROLE_ID", columnNames = {"ID"}),})public class Role implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "ID", nullable = false, updatable = false)    private Long id;    @Basic    @Column(name = "NAME", nullable = false)    private String name;    @Basic    @Convert(converter = AccessRightConverter.class)    @Column(name = "ACCESS_RIGHTS", nullable = false, columnDefinition = "text")    private List<RoleService.AccessRight> accessRights;    @Basic    @Column(name = "STATUS", nullable = false)    private Integer status;    @Basic    @CreationTimestamp    @JsonFormat(pattern = "dd-MMM-yyyy HH:mm:ss")    @Column(name = "CREATION_DATE", nullable = false)    private Timestamp creationDate;    @Basic    @UpdateTimestamp    @JsonFormat(pattern = "dd-MMM-yyyy HH:mm:ss")    @Column(name = "UPDATE_DATE", nullable = false)    private Timestamp updateDate;}