package com.ochibooh.demo.rmd.model;import com.fasterxml.jackson.annotation.JsonFormat;import lombok.Data;import lombok.EqualsAndHashCode;import lombok.ToString;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.DynamicUpdate;import org.hibernate.annotations.UpdateTimestamp;import javax.persistence.*;import java.io.Serializable;import java.sql.Timestamp;@ToString@Data@EqualsAndHashCode(of = {"id"})@DynamicUpdate@Entity@Table(name = "CONFIG_USER", uniqueConstraints = {        @UniqueConstraint(name = "CONFIG_USER_ID", columnNames = {"ID"}),        @UniqueConstraint(name = "CONFIG_USER_EMAIL", columnNames = {"EMAIL"}),})public class User implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "ID", nullable = false, updatable = false)    private Long id;    @Basic    @Column(name = "EMAIL", nullable = false)    private String email;    @Basic    @Column(name = "FIRST_NAME", nullable = false)    private String firstName;    @Basic    @Column(name = "LAST_NAME")    private String lastName;    @Basic    @Column(name = "PASSWORD", nullable = false)    private String password;    @Basic    @Column(name = "STATUS", nullable = false)    private Integer status;    @Basic    @CreationTimestamp    @JsonFormat(pattern = "dd-MMM-yyyy HH:mm:ss")    @Column(name = "CREATION_DATE", nullable = false)    private Timestamp creationDate;    @Basic    @UpdateTimestamp    @JsonFormat(pattern = "dd-MMM-yyyy HH:mm:ss")    @Column(name = "UPDATE_DATE", nullable = false)    private Timestamp updateDate;}