package com.ochibooh.demo.rmd.repo;import com.ochibooh.demo.rmd.model.Role;import com.ochibooh.demo.rmd.model.User;import com.ochibooh.demo.rmd.model.UserRole;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.lang.NonNull;import org.springframework.stereotype.Repository;import java.util.List;import java.util.Optional;@Repositorypublic interface UserRoleRepository extends JpaRepository<UserRole, Long> {    Optional<UserRole> findByUserAndRole(@NonNull User u, @NonNull Role r);    List<UserRole> findAllByUser(@NonNull User u);    List<UserRole> findAllByUserAndStatus(@NonNull User u, @NonNull Integer s);}