package com.ochibooh.demo.rmd.utils;import lombok.extern.java.Log;import org.aspectj.lang.ProceedingJoinPoint;import org.aspectj.lang.annotation.Around;import org.aspectj.lang.annotation.Aspect;import org.springframework.lang.NonNull;import org.springframework.stereotype.Component;import java.util.logging.Level;@Log@Aspect@Componentpublic class SharedUtils {    public String me() {        return "Hello World!";    }    @Around(value = "@annotation(com.ochibooh.demo.rmd.utils.LogExecutionTime)")    public Object logExecutionTime(@NonNull ProceedingJoinPoint joinPoint) throws Throwable {        long start = System.currentTimeMillis();        Object proceed = joinPoint.proceed();        long executionTime = System.currentTimeMillis() - start;        log.log(Level.INFO, String.format("%s -> Executed in %sms", joinPoint.getSignature(), executionTime));        return proceed;    }}