package com.ochibooh.demo.rmd.integ;import com.ochibooh.demo.rmd.data.role.RoleAccessRight;import lombok.extern.java.Log;import org.junit.jupiter.api.Test;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import java.util.UUID;import java.util.logging.Level;@Log@SpringBootTestpublic class RoleServiceTests {    @Autowired    private RoleService roleService;    @Test    public void testCreateRole() {        if (this.roleService.createRole(UUID.randomUUID().toString(), this.roleService.accessRights().stream().map(RoleAccessRight::getAccessRight).toList())) {            log.log(Level.INFO, "Test role created");        }    }}